            .supplyAsync(FuturesExample::fetchValue)
            .exceptionally(throwable -&gt; {
                if (throwable instanceof RuntimeException) {
                    return &quot;All good&quot;;
                }
                FutureExample.reThrow(throwable);
                // maybe a &quot;return null&quot; is necessary here (even when it is not reachable)
            });
}

