            for (Annotation annotation : annotations) {
                System.out.println(cls.getCanonicalName() + &quot;: &quot; + annotation.toString());
                if (annotation instanceof javax.persistence.Entity) {
                    classes.add(cls);
                }
            }
        }

        return classes;
