    private int noOfStudents = 0;
    
    // Counter that keep control of which thread is accessing the whiteboard
    private int counter = 0;

    // Whiteboard instance
    private static WhiteBoard wbInstance;
    // Private constructor
    private WhiteBoard() { }
    
