					Object[] metricParam = { metric };
					String[] metricSignature = { String.class.getName(), List.class.getName() };
					LOGGER.info(&quot;looking at individualMetric&quot;);
					individualMetric = mBeanServer.invoke(metricsName, &quot;metric&quot;, metricParam, metricSignature);
					LOGGER.info(individualMetric);
				} catch (Exception e) {
					LOGGER.error(e.getMessage());
				}
			}

