            case 1 : return &quot;Sunday&quot;; 
            case 2 : return &quot;Monday&quot;;
            case 3 : return &quot;Tuesday&quot;;
            case 4 : return &quot;Wednesday&quot;;
            case 5 : return &quot;Thursday&quot;;
            case 6 : return &quot;Friday&quot;;
        }
        
        return &quot;error!&quot;;
