      public static byte[] padding(String algorithm, byte[] hashBytes) throws Exception {
    
        //PREPARE PADDING
        byte[] padding = null;
        if (algorithm.equals(&quot;SHA-1&quot;  )) { padding = new byte[] { 0x30, 0x21, 0x30, 0x09, 0x06, 0x05, 0x2b, (byte) 0x0e, 0x03, 0x02, 0x1a, 0x05, 0x00, 0x04, 0x14                         }; }
