    private static int calculateHourglassSum(int[][] array, int rowIndex, int colIndex) {
        int sum = 0;
        for(int row = rowIndex; row &lt; rowIndex + 3; row++) {
            for(int col = colIndex; col &lt; colIndex + 3; col++) {
                if(row == rowIndex + 1 &amp;&amp; col != colIndex + 1) {
                    continue;
                }
                sum += array[row][col];
            }
        }
        return sum;
    }
    }

