public class Main {
    public static void main(String[] args) {
        String s = &quot;length 10 cm width 2 cm depth 0.5 cm&quot;;

        Pattern pattern = Pattern.compile(&quot;(\\w+\\s\\d+\\.?\\d*\\s\\w+)&quot;);
