    const JSONtoXML = (json, compact = false) =&gt; {
        const EOL = compact ? &#39;&#39; : &#39;\n&#39;
        const SOL = compact ? &#39;&#39; : &#39;  &#39;

        const objToXML = obj =&gt; {
            let xmlContent = &#39;&#39;

            if (typeof obj === &#39;object&#39;)
                Object.keys(obj).forEach(key =&gt; {
                    const value = obj[key]
                    
                    if (typeof value !== &#39;object&#39;)
                        return xmlContent += `${ SOL }&lt;${ key }&gt;${ value }&lt;/${ key }&gt;${ EOL }`

                    xmlContent += `&lt;${ key }&gt;${ EOL }`

                    if (Array.isArray(value)) {
                        const itemName = key.endsWith(&#39;s&#39;) ? key.slice(0, -1) : key

                        value.forEach(e =&gt; {
                            xmlContent += `${ SOL }&lt;${ itemName }&gt;${ EOL + SOL }${ objToXML(e).split(&#39;\n&#39;).join(EOL + SOL) }&lt;/${ itemName }&gt;${ EOL }`
                        })
                    } else {
                        let valXml = objToXML(value)

                        if (!compact)
                            valXml = SOL + valXml.split(&#39;\n&#39;).join(EOL + SOL)

                        xmlContent += valXml + EOL
                    }

                    xmlContent += `&lt;/${ key }&gt;`
                })

            else {
                xmlContent = obj.toString()
            }

            return xmlContent
        }

        return objToXML(JSON.parse(json))
    }

