        }
        
        private int findLastNonWordChar (String text, int index) {
            while (--index &gt;= 0) {
                if (String.valueOf(text.charAt(index)).matches(&quot;\\W&quot;)) {
                    break;
                }
            }
            return index;
        }
    
        private int findFirstNonWordChar (String text, int index) {
            while (index &lt; text.length()) {
                if (String.valueOf(text.charAt(index)).matches(&quot;\\W&quot;)) {
