    static void shiftByteBuffer(ByteBuffer bb, int numToShift) {
        bb.limit(bb.position());  // Make the buffer &quot;full&quot;
        bb.position(numToShift);  // Set position to numToShift so we only get rid of the first &lt;numToShift&gt; bytes.
