       private ContactType(String regex) {
         pattern = Pattern.compile(regex);
       }

       public void validateInput(String input) {
         if(!pattern.matcher(input).matches()) {
            throw new IllegalArgumentException(&quot;input invalid&quot;);
         }
       }
     }

