    void removeAll(StringBuilder sb, char c) {
      var length = sb.length();
      int i = 0;
      while (i &lt; length) {
        if (sb.charAt(i) == c) {
          sb.deleteCharAt(i);
          length--;
        } else {
          i++;
        }
      }
    }

