    // send broadcast address
    rt.exec(new String[]{
            &quot;ping&quot;,&quot;-n 1 -w 200 192.255.255.255&quot;
    });
    // interrogate arp cache
    Process p = rt.exec(new String[]{
            &quot;cmd.exe&quot;,&quot;/c&quot;,&quot;arp&quot;,&quot;-a&quot;
