                if (name.equals(SORT_PARAM_KEY)) {
                    String [] parts = super.getParameter(SORT_PARAM_KEY).split(&quot;,&quot;);
    				StringBuilder builder = new StringBuilder();
    				
    				int index = 0;
    				
    				for (String part : parts) {
    					// using some mechanism of you choosing
