        CompletableFuture&lt;OrderReponse&gt; orderResponse =
               RetryUtility.retry(() -&gt; makeOrder(orderInput, token), numRetries);
        orderResponse.join();
        return orderResponse;
    }

    public CompletableFuture&lt;OrderReponse&gt; makeOrder() {
