            minCpsField = new TextField();
            maxCpsField = new TextField();
            chooseKeyButton = new Button(&quot;Choose key&quot;);
            keyLabel = new Label(&quot;Key: not chosen&quot;);
    
            EventHandler&lt;KeyEvent&gt; keyEventEventHandler = event -&gt; {
                if (event.getCode() == triggerKey) {
