 */
zk.afterLoad(&#39;zul.inp&#39;, function() {
    var exWidget = {};
    zk.override(zul.inp.Comboitem.prototype, exWidget, {
        doClick_: function doClick_(evt) {
            if (!this._disabled) {
                var cb = this.parent;

                cb._select(this, {
                  sendOnSelect: true,
                  sendOnChange: true
                });

                this._updateHoverImage();
                if (this.getLabel() != &#39;show more&#39;){
