		Object[] obj = new Object[n];
		for (int i=0; i&lt;n; i++) {
			obj[i] = this.getItemAt(i);
		}
		return obj;
	}
	
	/** @return -1 if no item is true */
	int firstTrueItem() {
		int i = 0;
		for(Object obj : this.getItems()) {
			if(((ConditionalItem) obj).isEnabled()) return i;
			i++;
		}
		return -1;
	}
    }

