        private final MultiValueMap&lt;String, String&gt; queryParams;
    
        public QueryParamsUriBuilderFactory(String baseUrl, MultiValueMap&lt;String, String&gt; queryParams) {
            super(baseUrl);
            this.queryParams = queryParams;
        }
    
        @Override
        public UriBuilder builder() {
