        m.put(YIN, YANG);
        m.put(YANG, YIN);
        m.put(GOOD, EVIL);
        m.put(EVIL, GOOD);
        OPPOSITES = Collections.unmodifiableMap(m);
    }

    public Baz opposite() {
        return Objects.requireNonNull(OPPOSITES.get(this));
    }
}
