    List&lt;String&gt; list = new ArrayList&lt;&gt;();
    WebDriver driver = state.getDriver();
    WebElement errorBox = driver.findElement(By.id(state.getProperty(&quot;errorBoxGroupId&quot;)));
    for(WebElement div : errorBox.findElements(By.tagName(&quot;div&quot;))) {
