        public static &lt;V&gt; List&lt;String&gt; genPairs(Collection&lt;V&gt; col) {
          List&lt;V&gt; ls = ImmutableList.copyOf(col);
          List&lt;String&gt; ret = new ArrayList&lt;&gt;();
          
          for (int i = 0; i &lt; ls.size()-1; i++) {
            for (int j = i+1; j &lt; ls.size(); j++) {
              ret.add(ls.get(i)+&quot;, &quot;+ls.get(j));
            }
          }
        
          return ret;
        }

