    private static Map&lt;String, List&lt;Company&gt;&gt; topN(Map&lt;String, List&lt;Company&gt;&gt; map, int n) {
        return map.entrySet()
                  .stream()
                  .sorted(Comparator.comparingInt((Map.Entry&lt;String, List&lt;Company&gt;&gt; e) -&gt; e.getValue().size()).reversed())
