            DateTime then = now
                .withHourOfDay(hour)
                .withMinuteOfHour(0)
                .withSecondOfMinute(0);
            if (then.isAfter(now))
            {
                then = then.minusDays(1);
            }
            Period period = new Period(then, now, PeriodType.seconds());
            
            System.out.println(&quot;Difference in seconds: &quot; + period.getSeconds());
        }
    }


