    public static int pareAsBigEndianByteArray(byte[] bytes) {
        int factor = bytes.length - 1;
        int result = 0;
        for (int i = 0; i &lt; bytes.length; i++) {
            if (i == 0) {
                result |= bytes[i] &lt;&lt; (8 * factor--);
            } else {
                result |= bytes[i] &lt;&lt; (8 * factor--);
            }
        }
        return result;
    }

