import React from &#39;react&#39;;
import moment from &#39;moment&#39;;


const CountUpTimer = ({ startDate }) =&gt; {
    const currentTime = moment().unix()
    const jobStart = moment(startDate).unix()

    const startTime = currentTime - jobStart
    const [timer, setTimer] = React.useState(Number(startTime) || 0);

    let hour = React.useRef(0);
    let minute = React.useRef(0);
    let upSecond = React.useRef(0);

    const countTimer = React.useCallback(() =&gt; {
        setTimer(timer + 1);
        hour.current = Math.floor(timer / 3600);
        minute.current = Math.floor((timer - hour.current * 3600) / 60);
        upSecond.current = timer - (hour.current * 3600 + minute.current * 60);

    }, [timer]);


    React.useEffect(() =&gt; {
        const interval = setInterval(() =&gt; {
            countTimer()
        }, 1000);
        return () =&gt; clearInterval(interval);
    }, [timer, countTimer]);

    return &lt;div&gt;{hour.current}:{minute.current}:{upSecond.current}&lt;/div&gt;;
};

export default CountUpTimer;
