                    prepareStatementInsert.setString(4, String.valueOf(date.plusMonths(1)));
                    try{
                        prepareStatementInsert.executeUpdate();
                        return true;
                    }catch (Exception e) {
                        throw new RuntimeException(&quot;unhandled&quot;, e);
                    }
                }else{
                    return false;
                }
            }
        }catch (Exception e) {
            throw new RuntimeException(&quot;unhandled&quot;, e);
        }
        return false;
    }
    public int getDaysDiff(String dateStop) throws ParseException {
        LocalDate dt1 = LocalDate.now();
        LocalDate dt2 = LocalDate.parse(dateStop);

        long diffDays = ChronoUnit.DAYS.between(dt1, dt2);

        return (int)diffDays;
    }
}

