        private String email = &quot;whatever@gmail.com&quot;;
    
        private HospitalStaff hs;
    
        public Hospital(HospitalStaff hs) {
            this.hs = hs;  // &lt;- Associate the passed in HospitalStaff instance with this `Hospital` instance.
        }
    
