		      List&lt;WebElement&gt; cols = row.findElements(By.xpath(&quot;./mat-cell[@role=&#39;cell&#39;]/div/div[text()]&quot;));
		      for (WebElement col : cols) {
		          if (col.getText().equals(text)) {
			          driver.findElement(By.xpath(&quot;SOME XPATH AND + text + TEXT&quot;)).click();
		          }
		      }
	      }
	  }

