      NodeList styleNodes = svgDocument.getElementsByTagName(&quot;style&quot;);
      //if the original styles are not yet initialized,
      //retrieve them from document
      if (originalStylesContent ==null){
      ArrayList&lt;String&gt; styleContent=new ArrayList&lt;&gt;();
      for (int i=0; i&lt;styleNodes.getLength(); i++){
         styleContent.add(styleNodes.item(i).getTextContent());
      }
      originalStylesContent = styleContent.toArray(new String[0]);
