      String[] newStylesContent=updateCssProperties(originalStylesContent, owner);
    //      SVGSVGElement rootElement=svgDocument.getRootElement();
    //      SVGTransform st= rootElement.createSVGTransform();
      newStylesContent= updateCssRulesets(svgDocument, newStylesContent, owner);
      for (int i=0; i&lt;styleNodes.getLength(); i++){
         SVGStyleElement existingStyleNode = (SVGStyleElement) styleNodes.item(i);

         //BATIK does not seem to pick up changes to the content of style tags
         // when they are done through &quot;setTextContent&quot;
         //Therefore, I create a new style node and then replace the entire 
         //existing style node with the new style,
