         //Create a new style tag
         SVGStyleElement newStyle = (SVGStyleElement)svgDocument
                                      .createElementNS(SVGConstants.SVG_NAMESPACE_URI, &quot;style&quot;);
    //         newStyle.setAttributeNS(null, &quot;type&quot;, &quot;text/css&quot;);
