    class DefaultItemDiffCallback&lt;ItemType&gt; : DiffUtil.ItemCallback&lt;ItemType&gt;() {
    
        override fun areItemsTheSame(
            oldItem: ItemType,
            newItem: ItemType
        ): Boolean {
            return oldItem === newItem
        }
    
        override fun areContentsTheSame(
            oldItem: ItemType,
            newItem: ItemType
        ): Boolean {
            return oldItem.hashCode() == newItem.hashCode()
