    	Node(int val) {
    		this.val = val;
    	}
    }
    
    class BinarySearchTree {
    	Node root;
    	public void insert(int key) {
    		Node node = new Node(key);
    		if (root == null) {
    			root = node;
    			return;
    		}
    		Node prev = null;
    		Node temp = root;
    		while (temp != null) {
    			if (temp.val &gt; key) {
    				prev = temp;
    				temp = temp.left;
    			} else if (temp.val &lt; key) {
    				prev = temp;
    				temp = temp.right;
    			}
    		}
    		if (prev.val &gt; key)
    			prev.left = node;
    		else
    			prev.right = node;
    	}
    }

