    	public void replace(XWPFDocument document) {
    		List&lt;XWPFParagraph&gt; paragraphs = document.getParagraphs();
		
		for (XWPFParagraph xwpfParagraph : paragraphs) {
			replace(xwpfParagraph);
		}
