This kind of goes off of gt_ebuddy&#39;s answer using JavaCV, but my video output is at a much higher quality then his answer.  I&#39;ve also added some other random improvements (such as closing down the program when &lt;kbd&gt;ESC&lt;/kbd&gt; and &lt;kbd&gt;CTRL+C&lt;/kbd&gt; are pressed, and making sure to close down the resources the program uses properly).

    import java.awt.event.ActionEvent;
    import java.awt.event.KeyEvent;
    import java.awt.event.WindowAdapter;
    import java.awt.event.WindowEvent;
    import java.awt.image.BufferedImage;
    
    import javax.swing.AbstractAction;
    import javax.swing.ActionMap;
    import javax.swing.InputMap;
    import javax.swing.JComponent;
    import javax.swing.JFrame;
    import javax.swing.KeyStroke;
    
    import com.googlecode.javacv.CanvasFrame;
    import com.googlecode.javacv.OpenCVFrameGrabber;
    import com.googlecode.javacv.cpp.opencv_core.IplImage;
    
    public class HighRes extends JComponent implements Runnable {
    	private static final long serialVersionUID = 1L;
    	
    	private static CanvasFrame frame = new CanvasFrame(&quot;Web Cam&quot;);
    	private static boolean running = false;
    	private static int frameWidth = 800;
    	private static int frameHeight = 600;
    	private static OpenCVFrameGrabber grabber = new OpenCVFrameGrabber(0);
    	private static BufferedImage bufImg;
    
    	public HighRes()
    	{
    		// setup key bindings
    		ActionMap actionMap = frame.getRootPane().getActionMap();
    		InputMap inputMap = frame.getRootPane().getInputMap(JComponent.WHEN_IN_FOCUSED_WINDOW);
    
    		for (Keys direction : Keys.values())
    		{
    			actionMap.put(direction.getText(), new KeyBinding(direction.getText()));
    			inputMap.put(direction.getKeyStroke(), direction.getText());
    		}
    		
    		frame.getRootPane().setActionMap(actionMap);
    		frame.getRootPane().setInputMap(JComponent.WHEN_IN_FOCUSED_WINDOW, inputMap);
    
    		// setup window listener for close action
    		frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
    		frame.addWindowListener(new WindowAdapter()
    		{
    			public void windowClosing(WindowEvent e)
    			{
    				stop();
    			}
    		});
    	}
    
    	public static void main(String... args)
    	{
    		HighRes webcam = new HighRes();
    		webcam.start();
    	}
    
    	@Override
