    &lt;appender name=&quot;mask&quot; class=&quot;ch.qos.logback.core.ConsoleAppender&quot;&gt;
        &lt;encoder class=&quot;net.logstash.logback.encoder.LoggingEventCompositeJsonEncoder&quot;&gt;
            &lt;providers&gt;
                &lt;pattern&gt;
                    &lt;pattern&gt;
                        {
                            &quot;msg&quot;: &quot;%msg&quot;,
                            &quot;log_type&quot;: &quot;%level&quot;
                        }
                    &lt;/pattern&gt;
