   {
   
      public SOQ_20221222_1()
      {
      
         Long a = 100L;
         Long b = 100L;
        
         Long c = 101L;
         Long d = 101L;
        
         Long e = 102L;
         Long f = 102L;
        
         Long g = 126L;
         Long h = 126L;
        
         Long i = 127L;
         Long j = 127L;
        
         Long k = 128L;
         Long l = 128L;
      
         Long m = 129L;
         Long n = 129L;
      
         System.out.println(a == b);
         System.out.println(c == d);
      
         System.out.println(e == f);
         System.out.println(g == h);
      
         System.out.println(i == j);
         System.out.println(k == l);
      
         System.out.println(m == n);
         
      }
   
   }
   
