    // Initialize the variables that will help us navigate through the pyramid
    int row = 0;
    int col = rows / 2;
    boolean isDown = true;

    // Iterate through the list of numbers and build the pyramid
    for (int num : inputNumbers) {
        pyramid[row][col] = num;

        if (isDown) {
            row++;
        } else {
            row--;
            col--;
        }

        if (row == rows) {
            row = rows - 2;
            col++;
