      System.arraycopy(DECODER.decode(&quot;MIIEvAIBADANBgkqhkiG9w0BAQEFAASCBKY=&quot;), 0, result, 0, 26);
      System.arraycopy(BigInteger.valueOf(result.length - 4).toByteArray(), 0, result, 2, 2);
