    	&lt;binding dir=&quot;${dao.helter.dir}/src/resources/schema/helter&quot; includes=&quot;*.xsd&quot;/&gt;
    &lt;/wsimport&gt;
    &lt;copy todir=&quot;${dao.helter.dir}/build/bin/generated/com/helter/esp/dao/helter/jaxws/wsdl&quot;&gt;
