        public void add(InputStream stream) throws Exception{            
            inputs.add(stream);
            OPCPackage srcPackage = OPCPackage.open(stream);
            XWPFDocument src1Document = new XWPFDocument(srcPackage);         
            if(inputs.size() == 1){
                first = src1Document;
            } else {            
                CTBody srcBody = src1Document.getDocument().getBody();
