        InputStream in = socket.getInputStream();
        
        int readBytes = in.read( inData, 0, inData.length);

        if  ( Thread.interrupted() )
        {
            throw new InterruptedIOException( &quot;Thread interrupted during socket read&quot;);
        }
        
        return readBytes;
    }

