	private static &lt;T&gt; void permutation(Set&lt;T&gt; set, List&lt;T&gt; prefix, List&lt;List&lt;T&gt;&gt; results) {
		if (set.isEmpty()) {
			results.add(prefix);
			return;
		}
		for (T t : set) {
			Set&lt;T&gt; newSet = new HashSet&lt;&gt;(set);
			List&lt;T&gt; newPrefix = new ArrayList&lt;&gt;(prefix);
			newSet.remove(t);
			newPrefix.add(t);
			permutation(newSet, newPrefix, results);
		}
	} 

