		List&lt;int[]&gt; result = new ArrayList&lt;&gt;();
		int len=array.length;
		int arr[] = null;
		int size = splitSize;
		int k=0;
		for(int i=0;i&lt;array.length;i++) {
			if(k==0)
			arr=new int[size];
			arr[k]=array[i];
			k++;
			if(k&gt;size-1) {
				k=0;
				result.add(arr);
				len=len-size;
				if(len&lt;splitSize) {
					size=len;
				}
			}
		}
		return result;
	}

