                        mongoProperties.getAuthenticationDatabase(),
                        mongoProperties.getPassword());

        return MongoClients.create(MongoClientSettings.builder()
                .applyToClusterSettings(builder -&gt; builder
                        .hosts(serverAddresses)
                        .requiredReplicaSetName(mongoProperties.getReplicaSetName())
                        .localThreshold(localThreshold, TimeUnit.MILLISECONDS)
                        .serverSelectionTimeout(serverSelectionTimeout,TimeUnit.MILLISECONDS))
                .applyToSocketSettings(builder -&gt; builder
                        .connectTimeout(connectTimeout,TimeUnit.MILLISECONDS))
                .applyToSslSettings(builder -&gt; builder
                        .enabled(sslEnabled)
                        .invalidHostNameAllowed(sslInvalidHostNameAllowed))
                .applyToConnectionPoolSettings(builder -&gt; builder
                        .maxConnectionIdleTime(maxConnectionIdleTime,TimeUnit.MILLISECONDS)
                        .maxConnectionLifeTime(maxConnectionLifeTime,TimeUnit.MILLISECONDS)
                        .maxWaitTime(maxWaitTime,TimeUnit.MILLISECONDS))
                .applyToServerSettings(builder -&gt; builder
                        .minHeartbeatFrequency(minHeartbeatFrequency,TimeUnit.MILLISECONDS)
                        .heartbeatFrequency(heartbeatFrequency,TimeUnit.MILLISECONDS))
                .credential(credential)
                .build());
    }

