int order = requestedOrder.equals(&quot;asc&quot;) ? 1 : -1;

Collections.sort(List&lt;CustomObj&gt;, new Comparator&lt;CustomObj&gt;() {
    public int compare(CustomObj first, CustomObj scnd) {
