                int k = a.length - 1;
                while (a[j] &gt; a[k]) {
                    k--;
                }
    
                // Interchange a[j] and a[k]
    
                temp = a[k];
                a[k] = a[j];
                a[j] = temp;
    
                // Put tail end of permutation after jth position in increasing order
    
                int r = a.length - 1;
                int s = j + 1;
    
                while (r &gt; s) {
                    temp = a[s];
                    a[s] = a[r];
                    a[r] = temp;
                    r--;
                    s++;
                }
    
                numLeft = numLeft.subtract(BigInteger.ONE);
                return a;
    
            }
        }
    }

