    public class Combinations&lt;T&gt; implements Iterable&lt;List&lt;T&gt;&gt; {
    
        CombinationGenerator cGenerator;
        T[] elements;
        int[] indices;
    
        public Combinations(List&lt;T&gt; list, int n) {
